CREATE TABLE PERSONNE (NUM INTEGER AUTO_INCREMENT NOT NULL, NOM VARCHAR(255), PRENOM VARCHAR(255), PRIMARY KEY (NUM))
CREATE TABLE ADRESSE (RUE VARCHAR(255) NOT NULL, CODEPOSTAL VARCHAR(255), VILLE VARCHAR(255), PRIMARY KEY (RUE))
CREATE TABLE PERSONNE_ADRESSE (Personne_NUM INTEGER NOT NULL, adresses_RUE VARCHAR(255) NOT NULL, PRIMARY KEY (Personne_NUM, adresses_RUE))
ALTER TABLE PERSONNE_ADRESSE ADD CONSTRAINT FK_PERSONNE_ADRESSE_adresses_RUE FOREIGN KEY (adresses_RUE) REFERENCES ADRESSE (RUE)
ALTER TABLE PERSONNE_ADRESSE ADD CONSTRAINT FK_PERSONNE_ADRESSE_Personne_NUM FOREIGN KEY (Personne_NUM) REFERENCES PERSONNE (NUM)
